import { Gauge } from '@antv/g2plot';

const ticks = [0, 1 / 3, 2 / 3, 1];
const color = ['#F4664A', '#FAAD14', '#30BF78'];
const gauge = new Gauge('container', {
  percent: 0,
  innerRadius: 0.75,
  type: 'meter',
  // è‡ªå®šä¹‰ meter æ€»æ­¥æ•° ä»¥åŠ step å æ¯”
  meter: {
    steps: 50,
    stepRatio: 0.6,
  },
  range: {
    ticks: [0, 1],
    color: ['l(0) 0:#F4664A 0.5:#FAAD14 1:#30BF78'],
  },
  indicator: {
    pointer: {
      style: {
        stroke: '#D0D0D0',
      },
    },
    pin: {
      style: {
        stroke: '#D0D0D0',
      },
    },
  },
  statistic: {
    title: {
      formatter: ({ percent }) => {
        if (percent < ticks[1]) {
          return 'å·®';
        }
        if (percent < ticks[2]) {
          return 'ä¸­';
        }
        return 'ä¼˜';
      },
      style: ({ percent }) => {
        return {
          fontSize: '36px',
          lineHeight: 1,
          color: percent < ticks[1] ? color[0] : percent < ticks[2] ? color[1] : color[2],
        };
      },
    },
    content: {
      offsetY: 36,
      style: {
        fontSize: '24px',
        color: '#4B535E',
      },
      formatter: () => 'ç³»ç»Ÿè¡¨ç°',
    },
  },
});

gauge.render();
let data = 0.7;
const interval = setInterval(() => {
  if (data >= 1.5) {
    clearInterval(interval);
  }
  data += 0.005;
  gauge.changeData(data > 1 ? data - 1 : data);
}, 100);

