import { Mix } from '@antv/g2plot';

const expectData = [
  { value: 100, name: 'å±•ç°' },
  { value: 80, name: 'ç‚¹å‡»' },
  { value: 60, name: 'è®¿é—®' },
  { value: 40, name: 'å’¨è¯¢' },
  { value: 30, name: 'è®¢å•' },
];
const actualData = [
  { value: 80, name: 'å±•ç°' },
  { value: 50, name: 'ç‚¹å‡»' },
  { value: 30, name: 'è®¿é—®' },
  { value: 10, name: 'å’¨è¯¢' },
  { value: 5, name: 'è®¢å•' },
];

const plot = new Mix('container', {
  appendPadding: [8, 40, 8, 18],
  syncViewPadding: true,
  meta: {
    value: { sync: true },
  },
  tooltip: { shared: true, showMarkers: false, showTitle: false },
  plots: [
    {
      type: 'funnel',
