// @ts-nocheck
import { DataView } from '@antv/data-set';
import { Mix } from '@antv/g2plot';
import { keys, groupBy } from '@antv/util';

function generateYearData() {
  const r = [];
  for (let i = 1900; i <= 2016; i++) {
    r.push({ year: i });
  }
  return r;
}

fetch('https://gw.alipayobjects.com/os/antfincdn/NHer2zyRYE/nobel-prize-data.json')
  .then((data) => data.json())
  .then((originalData) => {
    const data = [...originalData.main, { year: 2016, number: 0, age: 0 }];
    let currentYear = 1901;
    const types = keys(groupBy(data, (d) => d.type));

    /** æ•£ç‚¹å›¾æ•°æ®ï¼ˆå„å›½è¯ºè´å°”å¥–è·å¥–è€…çš„å¹´é¾„åˆ†å¸ƒï¼‰ */
    const getPointViewData = (year) => {
      const r = data.map((d) => (d.year <= year ? d : { ...d, ageGroup: null })).filter((d) => d.age !== 0);
      const ds = new DataView().source(r);
      ds.transform({
        type: 'summary', // åˆ«å summary
        fields: ['number'], // ç»Ÿè®¡å­—æ®µé›†
        operations: ['sum'], // ç»Ÿè®¡æ“ä½œé›†
        as: ['number'], // å­˜å‚¨å­—æ®µé›†
        groupBy: ['country', 'ageGroup', 'type'], // åˆ†ç»„å­—æ®µé›†
      });
      return ds.rows;
    };

    /** å æ¯”ç¯å›¾æ•°æ®ï¼ˆå„é¢†åŸŸè¯ºè´å°”å¥–è·å¥–åˆ†å¸ƒï¼‰ */
    const getIntervalViewData = (year) => {
      const ds = new DataView().source(data.map((d) => (d.year <= year ? d : { ...d, number: 0 })));
      const othersCnt = data.filter((d) => d.year > year).reduce((a, b) => a + b.number, 0);
      ds.transform({
        type: 'summary', // åˆ«å summary
        fields: ['number'], // ç»Ÿè®¡å­—æ®µé›†
        operations: ['sum'], // ç»Ÿè®¡æ“ä½œé›†
        as: ['counts'], // å­˜å‚¨å­—æ®µé›†
        groupBy: ['type'], // åˆ†ç»„å­—æ®µé›†
      });
      return [...ds.rows, { type: 'å…¶å®ƒ', counts: othersCnt }];
    };

    const yearData = generateYearData();
    const plot = new Mix('container', {
      height: 500,
      padding: 'auto',
      appendPadding: [20, 0, 20, 0],
      legend: {
        type: { position: 'bottom' },
        number: false,
      },
      tooltip: {
        fields: ['country', 'age', 'number', 'ageGroup'],
        showMarkers: false,
        domStyles: {
          'g2-tooltip': {
            minWidth: '200px',
          },
          'g2-tooltip-list-item': {
            display: 'flex',
            justifyContent: 'space-between',
          },
        },
        customContent: (title, items) => {
          const datum = items[0]?.data || {};
          const fixed = (v) => v.toFixed(0);
          const tooltipItems = [
            { name: 'å¹´é¾„æ®µï¼š', value: `${fixed(Math.max(datum.ageGroup - 10, 0))}~${fixed(datum.ageGroup + 10)}` },
            { name: 'å¥–é¡¹å­¦ç§‘ï¼š', value: datum.type },
            { name: 'è·å¥–äººæ•°ï¼š', value: datum.number },
          ];
          let tooltipItemsStr = '';
          tooltipItems.forEach((item) => {
            tooltipItemsStr += `<li class="g2-tooltip-list-item">
               <span class="g2-tooltip-name">${item.name}</span>
               <span class="g2-tooltip-value">${item.value}</span>
             </li>`;
          });
          return `
             <div class="g2-tooltip-title">${title}</div>
             <ul class="g2-tooltip-list">${tooltipItemsStr}
             </ul>
          `;
        },
      },
      views: [
        {
          data: getIntervalViewData(currentYear),
          region: { start: { x: 0, y: 0.35 }, end: { x: 1, y: 0.65 } },
          coordinate: {
            type: 'theta',
            cfg: { innerRadius: 0.84, radius: 0.96 },
          },
          geometries: [
            {
              type: 'interval',
              yField: 'counts',
              colorField: 'type',
              mapping: {
                color: ({ type }) => {
                  const idx = types.indexOf(type);
                  const { colors10 = [] } = plot.chart.getTheme();
                  return colors10[idx] || '#D9D9D9';
                },
              },
              adjust: { type: 'stack' },
            },
          ],
          annotations: [
            {
              type: 'text',
              content: 'G2Plot',
              position: ['50%', '50%'],
              style: {
                textAlign: 'center',
                fontWeight: 400,
                fontSize: 28,
              },
            },
          ],
        },
        {
          data: getPointViewData(currentYear),
          region: { start: { x: 0, y: 0 }, end: { x: 1, y: 1 } },
          coordinate: {
            type: 'polar',
            cfg: {
              innerRadius: 0.45,
              radius: 0.64,
            },
          },
          axes: {
            country: {
              tickLine: null,
              label: null,
            },
            ageGroup: {
              tickLine: null,
              min: 20,
              max: 100,
              tickInterval: 20,
              title: {
                text: 'è·å¥–\nå¹´é¾„',
                autoRotate: false,
                offset: 12,
                style: { fontSize: 10, textBaseline: 'bottom' },
              },
              label: {
                offset: -4,
                style: {
                  textBaseline: 'bottom',
                  fontSize: 10,
                },
                formatter: (v) => (v === '100' ? '' : v),
              },
              grid: {
                line: {
                  style: {
                    lineWidth: 0.5,
                  },
                },
              },
            },
          },
          geometries: [
            {
              type: 'point',
              xField: 'country',
              yField: 'ageGroup',
              colorField: 'type',
              sizeField: 'number',
              adjust: {
                type: 'dodge',
              },
              mapping: {
                size: [2, 8],
                shape: 'circle',
                style: {
                  fillOpacity: 0.65,
                  lineWidth: 0,
                },
              },
            },
            {
              // å›½å®¶æ ‡ç­¾
              type: 'interval',
              xField: 'country',
              label: {
                labelEmit: true,
                fields: ['country'],
                offset: 50,
                style: {
                  fontSize: 10,
                },
              },
              mapping: {
                color: 'transparent',
              },
            },
          ],
        },
        {
          // å¹´åº¦ label å±•ç¤º
          data: yearData,
          region: {
            start: { x: 0.05, y: 0.05 },
            end: { x: 0.95, y: 0.95 },
          },
          axes: {
            year: {
              tickCount: 10,
              label: null,
              line: {
                style: {
                  lineWidth: 0.5,
                },
              },
            },
          },
          coordinate: { type: 'polar', cfg: { innerRadius: 0.99, radius: 1 } },
          geometries: [
            {
              type: 'line',
              xField: 'year',
              label: {
                labelEmit: true,
                content: ({ year }) => {
                  if (year === 1900) {
                    return '';
                  }
                  if (year === 2016) {
                    return ' ALL ';
                  }
                  return Number(year) % 10 === 0 ? year : '-';
                },
              },
              mapping: {
                color: 'transparent',
              },
            },
            {
              type: 'interval',
              xField: 'year',
              label: {
                labelEmit: true,
                fields: ['year'],
                callback: (year) => {
                  const { defaultColor } = plot.chart.getTheme();
                  return {
                    style: {
                      fill: year === currentYear ? 'rgba(255,255,255,0.85)' : 'transparent',
                    },
                    content: () => `${currentYear === 2016 ? ' ALL ' : currentYear}`,
                    background: {
                      padding: 2,
                      style: {
                        radius: 1,
                        fill: year === currentYear ? defaultColor : 'transparent',
                      },
                    },
                  };
                },
              },
              mapping: {
                color: 'transparent',
              },
            },
          ],
        },
      ],
    });

    plot.render();

    const view1 = plot.chart.views[0];
    const view2 = plot.chart.views[1];
    const view3 = plot.chart.views[2];
    // æ ¹æ® view3 ä¸­åˆ›å»ºçš„é¡ºåºï¼Œå¯çŸ¥ æ»‘å—å¯¹åº”çš„ç¬¬ 2 ä¸ªå‡ ä½•æ ‡è®°å¯¹è±¡ geometry
    const sliderBlock = view3.geometries[1];
    function rerender(specYear) {
      view1.changeData(getIntervalViewData(specYear));
      view2.changeData(getPointViewData(specYear));
      sliderBlock.label('year', (year) => {
        const { defaultColor } = plot.chart.getTheme();
        return {
          labelEmit: true,
          style: {
            fill: year === specYear ? 'rgba(255,255,255,0.85)' : 'transparent',
          },
          content: () => `${specYear === 2016 ? ' ALL ' : specYear}`,
          background: {
            padding: 2,
            style: {
              radius: 1,
              // éå½“å‰å¹´ä»½ï¼Œè¿›è¡Œé€æ˜å±•ç¤º
              fill: year === specYear ? defaultColor : 'transparent',
            },
          },
        };
      });
      // ä¼ å…¥å‚æ•° trueï¼Œé‡æ–°ç»˜åˆ¶ï¼Œä¸é‡æ–°è§¦å‘æ›´æ–°æµç¨‹ã€‚
      view3.render(true);
    }

    let interval;
    function start() {
      clearInterval(interval);
      interval = setInterval(() => {
        if (currentYear++ < 2016) {
          rerender(currentYear);
        } else {
          end();
        }
      }, 800);
    }
    function end() {
      clearInterval(interval);
      interval = null;
    }

    function handldSlideBlockClick(evt) {
      const data = evt.data?.data;
      if (data) {
        if (typeof data?.year === 'number') {
          currentYear = data.year;
          rerender(currentYear);
        }
      }
    }
    // ç›‘å¬ element click äº‹ä»¶ï¼ŒæŒ‡å®šå½“å‰å¹´ä»½ï¼Œå¹¶ä¸”å¯åŠ¨è½®æ’­
    view3.on('element:click', (evt) => {
      handldSlideBlockClick(evt);
      start();
    });
    // ç›‘å¬ element click äº‹ä»¶ï¼ŒæŒ‡å®šå½“å‰å¹´ä»½ï¼Œå¹¶ä¸”æš‚åœè½®æ’­
    view3.on('element:dblclick', (evt) => {
      handldSlideBlockClick(evt);
      end();
    });

    start();
  });

