        color: ({ area }) => {
          const value = data.find((d) => d.area === area)?.value;
          return value > 0.3 ? plot.chart.getTheme().defaultColor : defaultGrey;
        },
        barStyle: ({ value }) => {
          return {
            lineWidth: 1,
            fillOpacity: 0,
            stroke: value > 0.3 ? plot.chart.getTheme().defaultColor : defaultGrey,
          };
        },
      },
    },
    {
      region: { start: { x: 1 / 2, y: 1 / 2 }, end: { x: 1, y: 1 } },
      type: 'bar',
      options: {
        data, // å›¾è¡¨æ•°æ®
        meta: {
          value: {
            alias: 'é”€å”®é¢(ä¸‡)',
            min: 0,
            max: 1,
          },
        },
        yAxis: { label: { style: { fillOpacity: 0 } }, tickLine: null },
        xAxis: false,
        label: {
          offsetX: -4,
          position: 'left',
          layout: { type: 'adjust-color' },
          style: { fill: '#fff', fontSize: 12 },
          formatter: ({ area, value }) => {
            return `${area}\n${(value * 100).toFixed(1)}%`;
          },
        },
        yField: 'area',
        xField: 'value',
        color: ({ area }) => {
          const value = data.find((d) => d.area === area).value;
          return value > 0.3 ? plot.chart.getTheme().defaultColor : defaultGrey;
        },
        annotations: data.map((d, idx) => {
          return {
            type: 'line',
            start: [3 - idx - 0.25, 'min'],
            end: [3 - idx - 0.25, 'max'],
            style: {
              lineWidth: 2,
              stroke: '#595959',
            },
          };
        }),
      },
    },
  ],
  views: [
    {
      region: { start: { x: 0, y: 1 / 2 }, end: { x: 1 / 2, y: 1 } },
      data: [...data].reverse(), // å›¾è¡¨æ•°æ®
      meta: {
        value: {
          alias: 'é”€å”®é¢(ä¸‡)',
          max: 0.5,
          min: 0,
        },
      },
      axes: { area: { tickLine: null }, value: false },
      coordinate: { cfg: { isTransposed: true } },
      geometries: [
        {
          type: 'point',
          xField: 'area',
          yField: 'value',
          mapping: {
            color: ({ area }) => {
              const value = data.find((d) => d.area === area).value;
              return value > 0.3 ? plot.chart.getTheme().defaultColor : defaultGrey;
            },
            style: ({ value }) => {
              return {
                r: 4,
                strokeOpacity: 0,
                fill: value > 0.3 ? plot.chart.getTheme().defaultColor : defaultGrey,
              };
            },
          },
          label: {
            offsetY: -12,
            offsetX: 8,
            style: { textAlign: 'right' },
            position: 'top',
            formatter: ({ value }) => `${(value * 100).toFixed(1)}%`,
          },
        },
      ],
      // @ts-ignore
      annotations: [
        ...data.map((d) => {
          return {
            type: 'line',
            start: [d.area, 'min'],
            end: [d.area, 'max'],
            top: false,
            style: {
              lineWidth: 2,
              radius: 2,
              lineDash: [2, 4],
              stroke: defaultGrey,
            },
          };
        }),
      ],
    },
  ],
});
// Step 3: æ¸²æŸ“å›¾è¡¨
plot.render();

