import { each } from '@antv/util';
import { Mix } from '@antv/g2plot';

const datas = [
  [
    { company: 'Apple', type: 'æ•´ä½“', value: 30 },
    { company: 'Facebook', type: 'æ•´ä½“', value: 35 },
    { company: 'Google', type: 'æ•´ä½“', value: 28 },
  ],
  [
    { company: 'Apple', type: 'éæŠ€æœ¯å²—', value: 40 },
    { company: 'Facebook', type: 'éæŠ€æœ¯å²—', value: 65 },
    { company: 'Google', type: 'éæŠ€æœ¯å²—', value: 47 },
  ],
  [
    { company: 'Apple', type: 'æŠ€æœ¯å²—', value: 35 },
    { company: 'Facebook', type: 'æŠ€æœ¯å²—', value: 30 },
    { company: 'Google', type: 'æŠ€æœ¯å²—', value: 25 },
  ],
];

const plots = [];
const total = datas.length;
each(datas, (data, idx) => {
  const startX = idx / total;
  plots.push({
    type: 'column',
    region: {
      start: { x: startX, y: 0 },
      end: { x: (idx + 1) / total, y: 1 },
    },
    options: {
      data,
