      yField: 'value',
      seriesField: 'company',
      isGroup: true,
      meta: {
        value: {
          sync: true,
        },
      },
      xAxis: {
        label: {
          autoRotate: true,
        },
      },
      yAxis: idx === 0 ? { tickCount: 10 } : { label: { formatter: (v) => '' }, tickCount: 10 },
      tooltip: { showMarkers: false, fields: ['SalesTerritoryRegion', 'SalesAmount', 'quarter'] },
      // fixme è®¾ç½® yAxis label false ä¼šå¯¼è‡´
      // yAxis: idx === 0 ? {} : { label: false },
      minColumnWidth: 24,
      appendPadding: [20, 0],
      annotations: [
        {
          type: 'text',
          content: data[0].type,
          position: ['50%', '0%'],
          offsetY: -15,
          style: {
            textAlign: 'center',
          },
        },
      ],
    },
  });
});
const plot = new Mix('container', {
  plots,
  syncViewPadding: true,
  tooltip: false,
  legend: {},
});

plot.render();

