import { Facet } from '@antv/g2plot';
import { DataView } from '@antv/data-set';

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/diamond.json')
  .then((res) => res.json())
  .then((data) => {
    const plot = new Facet('container', {
      appendPadding: [0, 18, -50, 32],
      data,
      type: 'tree',
      fields: ['clarity'],
      meta: {
        cut: {
          sync: true,
          // æ‰‹åŠ¨è¿›è¡Œæ’åºï¼Œä¿è¯é¢œè‰²æ˜ å°„æ­£ç¡®
          values: ['Ideal', 'Good', 'Premium', 'Very-Good', 'Fair'],
        },
        mean: {
          sync: true,
          tickCount: 5,
          formatter: (v) => `${Math.ceil(v)}`,
        },
      },
      // tree-facet è¿æ¥çº¿æ ·å¼å’Œæ˜¯å¦å¹³æ»‘
      line: {
        style: {
          stroke: '#dedede',
        },
        smooth: true,
      },
      axes: {},
      tooltip: { showMarkers: false },
      eachView: (view, facet) => {
        const { rowIndex } = facet || {};
        const dv = new DataView();
        dv.source(facet.data).transform({
          type: 'aggregate',
          fields: ['price'],
          operations: ['mean'],
          as: ['mean'],
          groupBy: ['cut'],
        });
        if (rowIndex === 0) {
          return {
            type: 'pie',
            options: {
              data: dv.rows,
              meta: {
                mean: {
                  // å…³é—­é¥¼å›¾ 'mean'å­—æ®µçš„ scale åŒæ­¥
                  sync: false,
                },
              },
              angleField: 'mean',
              colorField: 'cut',
              radius: 1,
              pieStyle: { stroke: null },
              // æ·»åŠ åŠ¨ç”»
              animation: {},
              // æ·»åŠ äº¤äº’
              interactions: [{ type: 'element-active' }],
            },
          };
        }
        return {
          type: 'column',
          options: {
            data: dv.rows,
            meta: {
              mean: {
                // å…³é—­é¥¼å›¾ 'mean'å­—æ®µçš„ scale åŒæ­¥
                sync: rowIndex === 0 ? false : true,
              },
            },
            xField: 'cut',
            yField: 'mean',
            seriesField: 'cut',
            columnStyle: { fillOpacity: 0.85 },
            // æ·»åŠ åŠ¨ç”»
            animation: {},
            // æ·»åŠ äº¤äº’
            interactions: [{ type: 'element-active' }, { type: 'active-region' }],
          },
        };
      },
    });

    plot.render();
  });

