import { Pie, measureTextWidth } from '@antv/g2plot';

function renderStatistic(containerWidth, text, style) {
  const { width: textWidth, height: textHeight } = measureTextWidth(text, style);
  const R = containerWidth / 2;
  // r^2 = (w / 2)^2 + (h - offsetY)^2
  let scale = 1;
  if (containerWidth < textWidth) {
    scale = Math.min(Math.sqrt(Math.abs(Math.pow(R, 2) / (Math.pow(textWidth / 2, 2) + Math.pow(textHeight, 2)))), 1);
  }
  const textStyleStr = `width:${containerWidth}px;`;
  return `<div style="${textStyleStr};font-size:${scale}em;line-height:${scale < 1 ? 1 : 'inherit'};">${text}</div>`;
}

const data = [
  { type: 'åˆ†ç±»ä¸€', value: 27 },
  { type: 'åˆ†ç±»äºŒ', value: 25 },
  { type: 'åˆ†ç±»ä¸‰', value: 18 },
  { type: 'åˆ†ç±»å››', value: 15 },
  { type: 'åˆ†ç±»äº”', value: 10 },
  { type: 'å…¶ä»–', value: 5 },
];

const piePlot = new Pie('container', {
  appendPadding: 10,
  data,
  angleField: 'value',
  colorField: 'type',
  radius: 1,
  innerRadius: 0.64,
  meta: {
    value: {
      formatter: (v) => `${v} Â¥`,
    },
  },
  label: {
    type: 'inner',
    offset: '-50%',
    style: {
      textAlign: 'center',
    },
    autoRotate: false,
    content: '{value}',
  },
  statistic: {
    title: {
      offsetY: -4,
      customHtml: (container, view, datum) => {
        const { width, height } = container.getBoundingClientRect();
        const d = Math.sqrt(Math.pow(width / 2, 2) + Math.pow(height / 2, 2));
        const text = datum ? datum.type : 'æ€»è®¡';
        return renderStatistic(d, text, { fontSize: 28 });
      },
    },
    content: {
      offsetY: 4,
      style: {
        fontSize: '32px',
      },
      customHtml: (container, view, datum, data) => {
        const { width } = container.getBoundingClientRect();

        const text = datum ? `Â¥ ${datum.value}` : `Â¥ ${data.reduce((r, d) => r + d.value, 0)}`;
        return renderStatistic(width, text, { fontSize: 32 });
      },
    },
  },
  // æ·»åŠ  ä¸­å¿ƒç»Ÿè®¡æ–‡æœ¬ äº¤äº’
  interactions: [{ type: 'element-selected' }, { type: 'element-active' }, { type: 'pie-statistic-active' }],
});

piePlot.render();

