import { Funnel, FUNNEL_CONVERSATION_FIELD } from '@antv/g2plot';

const data = [
  { stage: 'ç®€å†ç­›é€‰', number: 253 },
  { stage: 'åˆè¯•äººæ•°', number: 151 },
  { stage: 'å¤è¯•äººæ•°', number: 113 },
  { stage: 'å½•å–äººæ•°', number: 87 },
  { stage: 'å…¥èŒäººæ•°', number: 59 },
];

const funnelPlot = new Funnel('container', {
  data: data,
  xField: 'stage',
  yField: 'number',
  isTransposed: true,
  minSize: 0.3,
  maxSize: 0.8,
  label: {
    formatter: (datum) => {
      // æä¾›å æ¯”$$percentage$$ï¼Œè½¬åŒ–ç‡$$conversion$$ä¸¤ç§æ ¼å¼
      return `${datum.stage}:${datum.number}`;
    },
  },
  conversionTag: {
    formatter: (datum) => {
      return (datum[FUNNEL_CONVERSATION_FIELD][1] / datum[FUNNEL_CONVERSATION_FIELD][0]).toFixed(2);
    },
  },
  tooltip: {
    formatter: (datum) => {
      return { name: datum.stage, value: `${datum.number}ä¸ª` };
    },
  },
});

funnelPlot.render();

