import React from 'react';
import ReactDOM from 'react-dom';
import { Select } from 'antd';
import { Column } from '@antv/g2plot';
import insertCss from 'insert-css';

class Plot extends React.Component {
  chartNodeRef = React.createRef();
  chartRef = React.createRef();

  componentDidMount() {
    fetch('https://gw.alipayobjects.com/os/antfincdn/PC3daFYjNw/column-data.json')
      .then((data) => data.json())
      .then((data) => {
        const chartDom = this.chartNodeRef.current;

        const plot = new Column(chartDom, {
          data,
          xField: 'city',
          yField: 'value',
          seriesField: 'type',
          isGroup: 'true',
        });

        plot.render();
        this.chartRef.current = plot;
      });
  }

  handlePositionChange = (v) => {
    const plot = this.chartRef.current;
    if (plot) {
      plot.update({ legend: { position: v } });
    }
  };

  render() {
    return (
      <section>
        <div>
          <span className="select-label">åˆ‡æ¢ä½ç½®</span>
          <Select
            aria-label="select"
            defaultValue="top-left"
            dropdownMatchSelectWidth={false}
            onChange={this.handlePositionChange}
            size="small"
          >
            {[
              'top',
              'top-left',
              'top-right',
              'left',
              'left-top',
              'left-bottom',
              'right',
              'right-top',
              'right-bottom',
              'bottom',
              'bottom-left',
              'bottom-right',
            ].map((opt) => {
              return <Select.Option value={opt}>{opt}</Select.Option>;
            })}
          </Select>
        </div>
        <div className={'chart-wrapper'} ref={this.chartNodeRef} />
      </section>
    );
  }
}

// æˆ‘ä»¬ç”¨ insert-css æ¼”ç¤ºå¼•å…¥è‡ªå®šä¹‰æ ·å¼
// æ¨èå°†æ ·å¼æ·»åŠ åˆ°è‡ªå·±çš„æ ·å¼æ–‡ä»¶ä¸­
// è‹¥æ‹·è´å®˜æ–¹ä»£ç ï¼Œåˆ«å¿˜äº† npm install insert-css
// æˆ‘ä»¬ç”¨ insert-css æ¼”ç¤ºå¼•å…¥è‡ªå®šä¹‰æ ·å¼
// æ¨èå°†æ ·å¼æ·»åŠ åˆ°è‡ªå·±çš„æ ·å¼æ–‡ä»¶ä¸­
// è‹¥æ‹·è´å®˜æ–¹ä»£ç ï¼Œåˆ«å¿˜äº† npm install insert-css
insertCss(`
  .select-label {
    margin-right: 8px;
  }
  .select-label:not(:first-of-type) {
    margin-left: 8px;
  }
  .chart-wrapper {
    margin-top: 12px;
    height: 320px;
  }
`);

ReactDOM.render(<Plot />, document.getElementById('container'));

