  { action: 'å®Œæˆäº¤æ˜“', pv: 8500 },
];

const plot = new Bar('container', {
  data: plotData,
  xField: 'pv',
  yField: 'action',
  conversionTag: {
    offset: 0,
    spacing: 8,
    arrow: {
      style: {
        fill: '#efefef',
      },
    },
  },
  interactions: [{ type: 'active-region', enable: false }, { type: 'conversion-tag-active' }],
});

plot.render();

